        #region date and time
        public string getDateTimeString(string dt, bool ignoreTime, bool showOnlyTime = false, bool addU = false)
        {
            DateTime dto;// = DateTime.Now;
            if (!DateTime.TryParse(dt, out dto))
            {
                var formatStrings = new string[] { "dd.MM.yyyy. HH:mm:ss", "d.MM.yyyy. HH:mm:ss", "dd.M.yyyy. HH:mm:ss", "d.M.yyyy. HH:mm:ss", "dd.MM.yyyy. H:mm:ss", "d.MM.yyyy. H:mm:ss", "dd.M.yyyy. H:mm:ss", "d.M.yyyy. H:mm:ss", "dd.MM.yyyy. HH:m:ss", "d.MM.yyyy. HH:m:ss", "dd.M.yyyy. HH:m:ss", "d.M.yyyy. HH:m:ss", "dd.MM.yyyy. H:m:ss", "d.MM.yyyy. H:m:ss", "dd.M.yyyy. H:m:ss", "d.M.yyyy. H:m:ss" };
                DateTime.TryParseExact(dt, formatStrings, CultureInfo.InvariantCulture, DateTimeStyles.None, out dto);
            }

            if (dto != null)
            {
                if (addU) return dto.ToString("dd.MM.yyyy. u HH:mm:ss");
                if (showOnlyTime) return dto.ToString("HH:mm");

                if (ignoreTime) return dto.ToString("dd.MM.yyyy.");
                else return dto.ToString("dd.MM.yyyy. HH:mm");
            }
            else
            {
                return "-";
            }
        }

        public string secondsToHours(int numOfSeconds)
        {
            string prefix = "";
            if (numOfSeconds < 0)
            {
                prefix = "-";
                numOfSeconds = Math.Abs(numOfSeconds);
            }
            TimeSpan ts = TimeSpan.FromSeconds(numOfSeconds);
            string hours = Math.Floor(ts.TotalHours).ToString();
            string minutes = "";
            if (ts.Minutes < 10) minutes = "0" + ts.Minutes;
            else minutes = ts.Minutes.ToString();

            return prefix + Math.Floor(ts.TotalHours) + ":" + minutes;
        }
        public string secondsToHMS(int numOfSeconds)
        {
            string prefix = "";
            if (numOfSeconds < 0)
            {
                prefix = "-";
                numOfSeconds = Math.Abs(numOfSeconds);
            }
            TimeSpan ts = TimeSpan.FromSeconds(numOfSeconds);
            string hours = Math.Floor(ts.TotalHours).ToString();
            string minutes = "";
            if (ts.Minutes < 10) minutes = "0" + ts.Minutes;
            else minutes = ts.Minutes.ToString();

            string seconds = "";
            if(ts.Seconds < 10) seconds = "0" + ts.Seconds;
            else seconds = ts.Seconds.ToString();

            return prefix + Math.Floor(ts.TotalHours) + ":" + minutes + ":" + seconds;
        }
        public DateTime getDateTime(string dt)
        {
            DateTime dto;// = DateTime.Now;
            if (!DateTime.TryParse(dt, out dto))
            {
                var formatStrings = new string[] { "dd.MM.yyyy. HH:mm:ss", "d.MM.yyyy. HH:mm:ss", "dd.M.yyyy. HH:mm:ss", "d.M.yyyy. HH:mm:ss", "dd.MM.yyyy. H:mm:ss", "d.MM.yyyy. H:mm:ss", "dd.M.yyyy. H:mm:ss", "d.M.yyyy. H:mm:ss", "dd.MM.yyyy. HH:m:ss", "d.MM.yyyy. HH:m:ss", "dd.M.yyyy. HH:m:ss", "d.M.yyyy. HH:m:ss", "dd.MM.yyyy. H:m:ss", "d.MM.yyyy. H:m:ss", "dd.M.yyyy. H:m:ss", "d.M.yyyy. H:m:ss" };
                DateTime.TryParseExact(dt, formatStrings, CultureInfo.InvariantCulture, DateTimeStyles.None, out dto);
            }
            return dto;
        }

        #endregion

        #region numbers

        public string getFormatedNumber(decimal value, string format = "#,##0.00", NumberFormatInfo nfi = null)
        {
            if (String.IsNullOrWhiteSpace(format)) return value.ToString();
            if (nfi == null)
            {
                nfi = new NumberFormatInfo();
                nfi.NumberDecimalSeparator = ",";
                nfi.NumberGroupSeparator = ".";
            }
            return value.ToString(format, nfi);
        }
        public string getFormatedNumber(string value, string format = "#,##0.00", NumberFormatInfo nfi = null)
        {
            //"0.00"
            //"#,##0.00"
            if (value == null) return "";
            float parsedValue = 0;
            if (float.TryParse(value, out parsedValue))
            {
                return getFormatedNumber((decimal)parsedValue, format, nfi);
            }
            else return getFormatedNumber((decimal)parsedValue, format, nfi);
        }

        public string getFormatedNumber(double value, string format = "#,##0.00", NumberFormatInfo nfi = null)
        {
            try
            {
                return getFormatedNumber((decimal)value, format, nfi);
            }
            catch (Exception)
            {
                
            }
            return "";
            
        }
        #endregion

        #region checkers
        public bool isValidCoordinate(float lat, float lon)
        {
            if (lat == 0 && lon == 0) return false;
            if (Math.Abs(lat) > 90) return false;
            if (Math.Abs(lon) > 180) return false;
            return true;
        }
